import React, { useState, useEffect } from 'react';
import { getUsers } from '../api/userApi';

function UserList() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        async function fetchUsers() {
            try {
                const usersData = await getUsers();
                setUsers(usersData);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        }
        fetchUsers();
    }, []);

    return (
        <div>
            <h1>User List</h1>
            <table>
                <thead>
                    <tr>
                        <th>Username</th>
                        <th>Company</th>
                        <th>Validation Status</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.profile ? user.profile.company : ''}</td> {/* Accès à la propriété company via le profil s'il existe */}
                            <td>{user.validation ? (user.validation.is_validated ? 'Accepted' : 'Pending') : ''}</td> {/* Accès à la propriété is_validated via la validation s'il existe */}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default UserList;
