import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './styles/home.css';

const Home = () => {
    const [companies, setCompanies] = useState([]);
    const [newCompanyName, setNewCompanyName] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:8000/api/companies/')
            .then(response => setCompanies(response.data))
            .catch(error => console.error('Error fetching companies:', error));
    }, []);

    const handleCreateCompanyClick = () => {
        if (newCompanyName.trim() !== '') {
            axios.post('http://localhost:8000/api/create-company/', { company: newCompanyName })
                .then(response => {
                    setCompanies([...companies, response.data]);
                    setNewCompanyName('');
                })
                .catch(error => console.error('Error creating company:', error));
        }
    };

    const deleteCompany = (id) => {
        axios.delete(`http://localhost:8000/api/companies/${id}/`)
            .then(() => setCompanies(companies.filter(company => company.id !== id)))
            .catch(error => console.error('Error deleting company:', error));
    };

    const viewCompany = (id) => {
        navigate(`/analysis/${id}`);
    };

    return (
        <div className="home-container">
            <h1>Customer</h1>
            <div className="company-section">
                <label htmlFor="company-search">Search for a company</label>
                <input 
                    type="text" 
                    id="company-search" 
                    placeholder="Company" 
                    value={newCompanyName} 
                    onChange={(e) => setNewCompanyName(e.target.value)} 
                />
                <button className="create-company-btn" onClick={handleCreateCompanyClick}>Create new company</button>
            </div>
            <div className="company-table">
                <table>
                    <thead>
                        <tr>
                            <th>Company name</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {companies.map(company => (
                            <tr key={company.id}>
                                <td>{company.name}</td>
                                <td>{company.status}</td>
                                <td>
                                    <button onClick={() => viewCompany(company.id)}>
                                        <img src="../images/eye-icon.png" alt="View" />
                                    </button>
                                    <button onClick={() => deleteCompany(company.id)}>
                                        <img src="../images/delete.png" alt="Delete" />
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <button className="download-btn" onClick={() => navigate('/documents')}>Download the documents</button>
        </div>
    );
};

export default Home;
