import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getUsers } from '../api/userApi';
import acceptedLogo from '../images/accepted.png';
import pendingLogo from '../images/pending.png';
import './styles/userList.css';

function UserList() {
    const [users, setUsers] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchUsers() {
            try {
                const usersData = await getUsers();
                setUsers(usersData);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        }
        fetchUsers();
    }, []);

    const handlePendingClick = (userId) => {
        navigate(`/validate-user/${userId}`);
    };
    
    
    return (
        <div className="userListContainer">
            <h1 className="userListTitle">Admin Page</h1>
            <table className="userTable">
                <thead>
                    <tr>
                        <th>Customer</th>
                        <th>Company</th>
                        <th>Admission Status</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, index) => (
                        <tr key={index}>
                            <td>{user.username}</td>
                            <td>{user.company}</td>
                            <td>
                                {user.validation ? (
                                    user.validation.is_validated ? (
                                        <span>
                                            <img src={acceptedLogo} alt="Accepted" className="statusLogo" />
                                            Accepted
                                        </span>
                                    ) : (
                                        <span onClick={() => handlePendingClick(user.id)}>
                                            <img src={pendingLogo} alt="Pending" className="statusLogo" />
                                            Pending
                                        </span>
                                    )
                                ) : (
                                    <span onClick={() => handlePendingClick(user.id)}>
                                        <img src={pendingLogo} alt="Pending" className="statusLogo" />
                                        Pending
                                    </span>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default UserList;
