import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/authContext.jsx';
import Login from './components/login.jsx';
import Register from './components/signup.jsx';
import ForgotPassword from './components/forgotPassword.jsx';
import UserList from './components/userList.jsx';
import ValidateUser from './components/ValidateUser.jsx';
import Home from './components/home.jsx';
import ProtectedRoute from './components/ProtectedRoute.jsx';

function App() {
  const { user } = useAuth(); // Suppose que vous avez une fonction useAuth qui retourne les données de l'utilisateur et que isAuthenticated est un attribut de l'utilisateur qui indique si l'utilisateur est authentifié

  return (
    <div className='app'>
      <AuthProvider>
        <Router>
          <Routes>
            <Route path='/' element={<Login />} />
            <Route path='/signup' element={<Register />} />
            <Route path='/forgot-password' element={<ForgotPassword />} />
            <ProtectedRoute
              path='/users'
              element={<UserList />}
              isAuthenticated={user.logged} // Vérifiez si l'utilisateur est authentifié
            />
            <ProtectedRoute
              path="/validate-user/:userId"
              element={<ValidateUser />}
              isAuthenticated={user.logged}
            />
            <ProtectedRoute
              path='/home'
              element={<Home />}
              isAuthenticated={user.logged}
            />
          </Routes>
        </Router>
      </AuthProvider>
    </div>
  );
}

export default App;
