import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import acceptedLogo from '../images/accepted.png';
import pendingLogo from '../images/pending.png';

function ValidateUser() {
    const { userId } = useParams();
    const navigate = useNavigate();
    const [validationStatus, setValidationStatus] = useState(null);

    const handleValidation = async (isValidated) => {
        try {
            // Assurez-vous que userId est défini avant de continuer
            if (userId) {
                const response = await axios.patch(`http://127.0.0.1:8000/api/validate/${userId}/`, { is_validated: isValidated });
                console.log('Validation status updated:', response.data);
                setValidationStatus(isValidated ? 'Accepted' : 'Rejected');
                navigate('/users'); // Rediriger vers la liste des utilisateurs
            } else {
                console.error('Error: userId is undefined');
            }
        } catch (error) {
            console.error('Error updating validation status:', error);
        }
    };

    return (
        <div className="validationContainer">
            <h1>Validate User</h1>
            {/* Afficher le nom de la société */}
            <p>Company Name: nomdelaCompany</p>
            {/* Afficher le nom d'utilisateur */}
            <p>Username: </p>
            {/* Afficher les logos */}
            <img src={validationStatus === 'Accepted' ? acceptedLogo : pendingLogo} alt={validationStatus} className="statusLogo" />
            {/* Boutons de validation avec les vrais noms */}
            <button onClick={() => handleValidation(true)}>Accept</button>
            <button onClick={() => handleValidation(false)}>Reject</button>
        </div>
    );
}

export default ValidateUser;
