import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';

const Login = () => {
  const [userLogin, setUserLogin] = useState({ usr: '', pwd: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserLogin({ ...userLogin, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!userLogin.usr || !userLogin.pwd) {
      setError('Username and password are required');
      return;
    }

    const data = {
      username: userLogin.usr,
      password: userLogin.pwd,
    };

    try {
      const response = await axios.post('http://127.0.0.1:8000/api/login/', data);
      const { token, is_admin, is_validated } = response.data;
      
      // Stockage du token dans le localStorage
      localStorage.setItem('authToken', token);

      // Redirection en fonction du rôle de l'utilisateur
      if (is_admin) {
        navigate('/users');
      } else if (is_validated) {
        navigate('/home');
      } else {
        setError('User validation pending. Please contact admin.');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('Invalid username or password');
    }
  };

  return (
    <div className="loginDiv">
      <form onSubmit={handleSubmit}>
        <h1 className="welcomeTitle">Welcome</h1>
        {error && <div>{error}</div>}
        <span className="inputWithIcon">
          <input
            type="text"
            name="usr"
            placeholder="Username"
            onChange={handleChange}
          />
        </span>
        <br />
        <span className="inputWithIcon">
          <input
            type="password"
            name="pwd"
            placeholder="Password"
            onChange={handleChange}
          />
        </span>
        <br />
        <div className="buttonGroup">
          <button type="submit">Login</button>
        </div>
      </form>
    </div>
  );
};

export default Login;
