import React, { useState } from 'react';
import './styles/home.css';

const Home = () => {
    const [companies, setCompanies] = useState([
        { name: 'BANQUE LCL', status: 'Pending' },
        { name: 'BNP PARIBAS', status: 'Downloaded' },
        { name: 'CREDIT MUTUEL', status: 'Pending' },
    ]);

    return (
        <div className="home-container">
            <h1>Customer</h1>
            <div className="company-section">
                <label htmlFor="company-search">Search for a company</label>
                <input type="text" id="company-search" placeholder="Company" />
                <button className="create-company-btn">Create new company</button>
            </div>
            <div className="company-table">
                <table>
                    <thead>
                        <tr>
                            <th>Company name</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {companies.map((company, index) => (
                            <tr key={index}>
                                <td>{company.name}</td>
                                <td>
                                    {company.status}
                                    <button className="status-btn">
                                        {company.status === 'Downloaded' ? '✔' : '✖'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <button className="download-btn">Download the documents</button>
        </div>
    );
};

export default Home;
