import './styles/login.css';
import { useState } from "react";
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/authContext';
import user from '../images/user.png';
import pwdPhoto from '../images/cadena.png';
import CryptoJS from 'crypto-js';


function Login() {
    const [userLogin, setUserLogin] = useState({
        usr: "",
        pwd: ""
    });
    const {login, user, setUser} = useAuth();
    const navigate = useNavigate();
    const [error, setError] = useState("");
    const handleChange = (e) => {
        const {name, value} = e.target;
        setUserLogin({
            ...userLogin,
            [name]: value
        });
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!userLogin.usr || !userLogin.pwd) {
            setError("Username is required");
            return;
        } 
        const pwd = CryptoJS.enc.Base64.stringify(CryptoJS.SHA256(userLogin.pwd));
        const data = {
            username: userLogin.usr,
            password: pwd
        }
        try {
            await login(data);
            navigate('/projects');
        } catch (error) {
            setError(error.message);
        }
    }


    return (
        <div className='loginDiv'>
            <form onSubmit={handleSubmit}>
            <h1 className='welcomeTitle'>Welcome</h1>
                {error && <div>{error}</div>}
                <span>
                    <figure>
                        <img src={user} alt="usr" />
                    </figure>
                    <input type="text" name="usr" placeholder="Username" onChange={handleChange}/>
                </span>
                <br/>
                <span>
                    <figure>
                        <img src={pwdPhoto} alt="pwd" />
                    </figure>
                    <input type="password" name="pwd" placeholder="Password" onChange={handleChange}/>
                </span>
                <a className='forgot' href="/">Forgot password?</a>
                <br/>
                <button>Login</button>
                <Link className='signup' to={'/signup'}>Sign up</Link>
            </form>
        </div>
    )
}

export default Login;
