import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import acceptedLogo from '../images/accepted.png';
import pendingLogo from '../images/pending.png';

function ValidateUser() {
    const { userId } = useParams();
    const navigate = useNavigate();
    const [user, setUser] = useState(null);

    useEffect(() => {
        async function fetchUserData() {
            try {
                const response = await axios.get(`http://localhost:8000/api/user-details/${userId}/`);
                const userData = response.data;
                console.log(userData); // Vérifiez les données récupérées
                setUser(userData);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        }
    
        fetchUserData();
    }, [userId]);

    const handleValidation = async (isValidated) => {
        try {
            if (userId) {
                const response = await axios.patch(`http://127.0.0.1:8000/api/validate/${userId}/`, { is_validated: isValidated });
                console.log('Validation status updated:', response.data);
                navigate('/users');
            } else {
                console.error('Error: userId is undefined');
            }
        } catch (error) {
            console.error('Error updating validation status:', error);
        }
    };

    return (
        <div className="validationContainer">
            {user && (
                <>
                    <h1>Validate User</h1>
                    <p>Company Name: {user.company}</p>
                    <p>Username: {user.username}</p>
                    <img src={user.validation.is_validated ? acceptedLogo : pendingLogo} alt={user.validation.is_validated ? 'Accepted' : 'Pending'} className="statusLogo" />
                    <button onClick={() => handleValidation(true)}>Accept</button>
                    <button onClick={() => handleValidation(false)}>Reject</button>
                </>
            )}
        </div>
    );
}

export default ValidateUser;
