import "./styles/login.css";
import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/authContext";
import User from "../images/user.png";
import Cadena from "../images/cadena.png";
import EyeIcon from "../images/eye-icon.png";
import EyeSlashIcon from "../images/eye-slash-icon.png";
import CryptoJS from "crypto-js";

function Login() {
  const [userLogin, setUserLogin] = useState({
    usr: "",
    pwd: "",
  });
  const { login, user, setUser } = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserLogin({
      ...userLogin,
      [name]: value,
    });
  };

  const togglePasswordVisibility = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userLogin.usr || !userLogin.pwd) {
      setError("Username is required");
      return;
    }
    const pwd = CryptoJS.enc.Base64.stringify(CryptoJS.SHA256(userLogin.pwd));
    const data = {
      username: userLogin.usr,
      password: pwd,
    };
    try {
      await login(data);
      navigate("/projects");
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="loginDiv">
      <form onSubmit={handleSubmit}>
        <h1 className="welcomeTitle">welcome</h1>
        {error && <div>{error}</div>}
        <span className="inputWithIcon">
          <input
            type="text"
            name="usr"
            placeholder="Username"
            onChange={handleChange}
          />
          <img src={User} alt="usr" className="icon" />
        </span>

        <br />
        <span className="inputWithIcon">
  <input
    type={showPassword ? "text" : "password"}
    name="pwd"
    placeholder="Password"
    onChange={handleChange}
  />
  <img
  src={showPassword ? EyeSlashIcon : EyeIcon}
  alt="Toggle Password Visibility"
  className={`icon eyeIcon ${showPassword ? "rightIcon" : ""}`}
  onClick={togglePasswordVisibility}
/>

  <img src={Cadena} alt="pwd" className={`icon cadenaIcon ${showPassword ? "" : "leftIcon"}`} />
</span>
        <a className="forgot" href="/">
          Forgot password?
        </a>
        <br />
        <div className="buttonGroup">
          <button>Login</button>
          <Link className="signup" to={"/signup"}>
            Sign up
          </Link>
        </div>
      </form>
    </div>
  );
}

export default Login;
