import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/authContext';
import Login from './components/login';
import Register from './components/signup';
import ForgotPassword from './components/forgotPassword';
import UserList from './components/userList';
import ValidateUser from './components/ValidateUser'; // Importez le composant ValidateUser avec une majuscule

function App() {
  return (
    <div className='app'>
      <AuthProvider>
        <Router>
          <Routes>
            <Route path='/' element={<Login />} />
            <Route path='/signup' element={<Register />} />
            <Route path='/forgot-password' element={<ForgotPassword />} />
            <Route path='/users' element={<UserList />} />
            <Route path="/validate-user/:userId" element={<ValidateUser />} /> {/* Assurez-vous que le chemin de la route correspond à celui défini dans le composant ValidateUser */}
          </Routes>
        </Router>
      </AuthProvider>
    </div>
  );
}

export default App;
