import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000/api/users/';

// Fonction pour obtenir les utilisateurs
export async function getUsers() {
    try {
        // Ajoutez { withCredentials: true } pour utiliser les cookies
        const response = await axios.get(API_URL, { withCredentials: true });
        return response.data;
    } catch (error) {
        throw new Error('Failed to fetch users');
    }
}

// Fonction pour mettre à jour un utilisateur
export async function updateUser(userId, updatedData) {
    const url = `${API_URL}${userId}/`;
    try {
        // Ajoutez { withCredentials: true } pour utiliser les cookies
        const response = await axios.patch(url, updatedData, { withCredentials: true });
        return response.data;
    } catch (error) {
        throw new Error(`Failed to update user with ID ${userId}`);
    }
}

// Fonction pour obtenir un utilisateur par son ID
export async function getUserById(userId) {
    const url = `${API_URL}${userId}/`;
    try {
        // Ajoutez { withCredentials: true } pour utiliser les cookies
        const response = await axios.get(url, { withCredentials: true });
        return response.data;
    } catch (error) {
        throw new Error(`Failed to fetch user with ID ${userId}`);
    }
}

// Fonction pour enregistrer un nouvel utilisateur
const API_REGISTER_URL = 'http://127.0.0.1:8000/api/register/';
export async function registerUser(userData) {
    try {
        // Ajoutez { withCredentials: true } pour utiliser les cookies
        const response = await axios.post(API_REGISTER_URL, userData, { withCredentials: true });
        return response.data;
    } catch (error) {
        throw new Error('Failed to register user');
    }
}
