import { createContext, useContext, useState } from "react";
import axios from 'axios';

export const authContext = createContext(); /* Création du contexte d'authentification (authContext) */

export const useAuth = () => {
    const context = useContext(authContext);
    if (!context) throw new Error('There is no AuthProvider');
    return context;
}

const client = axios.create({
    baseURL: 'http://127.0.0.1:8000/api/',
    withCredentials: true, // Autoriser Axios à envoyer les cookies de session
});

export function AuthProvider({ children }) {
    const [loading, setLoading] = useState(false);
    const [user, setUser] = useState({
        logged: false,
        data: [],
    });

    const login = async (data) => {
        setLoading(true);
        try {
            const res = await client.post('login/', data);
            if (res.status === 200) {
                setUser({ logged: true, data: res.data });
            } else {
                throw new Error(res.data.detail);
            }
        } catch (err) {
            console.error(err);
            throw new Error('Login failed');
        } finally {
            setLoading(false);
        }
    }

    const logout = async () => {
        if (!user.logged) return console.log("User is not logged");
        setLoading(true);
        try {
            await client.post('logout/');
            setUser({ logged: false, data: [] });
        } catch (err) {
            console.error(err);
            throw new Error('Logout failed');
        } finally {
            setLoading(false);
        }
    }

    const signup = async (data) => {
        setLoading(true);
        try {
            const res = await client.post('register/', data);
            return res.data;
        } catch (err) {
            console.error(err);
            throw new Error('Signup failed');
        } finally {
            setLoading(false);
        }
    }

    const resetPassword = async (email) => {
        setLoading(true);
        try {
            const res = await client.post('/reset-password', { email });
            return res.data; // Return the response data
        } catch (err) {
            console.error(err);
            throw new Error('An error occurred while resetting the password');
        } finally {
            setLoading(false);
        }
    };

    const contextValue = { login, logout, user, setUser, signup, resetPassword, loading };

    return (
        <authContext.Provider value={contextValue}>
            {children}
        </authContext.Provider>
    );
}
