import React, { useState, useEffect } from 'react';
import './styles/home.css';
import axios from 'axios';

const Home = () => {
    const [companies, setCompanies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        const fetchCompanies = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/companies/', {
                    headers: {
                        Authorization: `Token ${localStorage.getItem('token')}`
                    }
                });
                setCompanies(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching companies:', error);
                setError("Error fetching companies");
                setLoading(false);
            }
        };

        fetchCompanies();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div className="home-container">
            <h1>Customer</h1>
            <div className="company-section">
                <label htmlFor="company-search">Search for a company</label>
                <input type="text" id="company-search" placeholder="Company" />
                <button className="create-company-btn">Create new company</button>
            </div>
            <div className="company-table">
                <table>
                    <thead>
                        <tr>
                            <th>Company name</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {companies.map((company, index) => (
                            <tr key={index}>
                                <td>{company.name}</td>
                                <td>
                                    {company.status}
                                    <button className="status-btn">
                                        {company.status === 'Downloaded' ? '✔' : '✖'}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <button className="download-btn">Download the documents</button>
        </div>
    );
};

export default Home;
