import "./styles/login.css";
import { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { useAuth } from "../context/authContext";
import User from "../images/user.png";
import Cadena from "../images/cadena.png";
import EyeIcon from "../images/eye-icon.png";
import EyeSlashIcon from "../images/eye-slash-icon.png";
import axios from 'axios';

function Login() {
  const [userLogin, setUserLogin] = useState({
    usr: "",
    pwd: "",
  });
  const [rememberMe, setRememberMe] = useState(false);
  const { login, user, setUser } = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserLogin({
      ...userLogin,
      [name]: value,
    });
  };

  const togglePasswordVisibility = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!userLogin.usr || !userLogin.pwd) {
      setError("Username and password are required");
      return;
    }
    
    const data = {
      username: userLogin.usr,
      password: userLogin.pwd, // Enlever le cryptage côté client
    };
    
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/login/', data);
      const { token } = response.data;
      // Stocker le token ou le traiter selon votre logique d'authentification
      if (rememberMe) {
        localStorage.setItem("token", token);
      } else {
        sessionStorage.setItem("token", token);
      }
      navigate("/home");
    } catch (error) {
      setError(error.response.data.error);
    }
};


  return (
    <div className="loginDiv">
      <form onSubmit={handleSubmit}>
        <h1 className="welcomeTitle">welcome</h1>
        {error && <div>{error}</div>}
        <span className="inputWithIcon">
          <input
            type="text"
            name="usr"
            placeholder="Username"
            onChange={handleChange}
          />
          <img src={User} alt="usr" className="icon userIcon" />
        </span>

        <br />
        <span className="inputWithIcon">
          <input
            type={showPassword ? "text" : "password"}
            name="pwd"
            placeholder="Password"
            onChange={handleChange}
          />
          <img
            src={showPassword ? EyeSlashIcon : EyeIcon}
            alt="Toggle Password Visibility"
            className={`icon eyeIcon ${showPassword ? "rightIcon" : ""}`}
            onClick={togglePasswordVisibility}
          />
          <img src={Cadena} alt="pwd" className={`icon cadenaIcon ${showPassword ? "" : "leftIcon"}`} />
        </span>

        <div className="options">
          <div className="rememberMe">
            <input
              type="checkbox"
              id="rememberMe"
              name="rememberMe"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
            />
            <label htmlFor="rememberMe">Remember me</label>
          </div>
          <Link className="forgot" to="/forgot-password">Forgot password?</Link>
        </div>

        <br />
        <div className="buttonGroup">
          <button>Login</button>
          <Link className="signup" to={"/signup"}>
            Sign up
          </Link>
        </div>
      </form>
    </div>
  );
}

export default Login;
