import React, { useState } from 'react';
import { useAuth } from '../context/authContext';

function Profile() {
    const { user } = useAuth();
    const [newEmail, setNewEmail] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');

    const handleEmailChange = (e) => {
        setNewEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setNewPassword(e.target.value);
    };

    const handleConfirmPasswordChange = (e) => {
        setConfirmPassword(e.target.value);
    };

    const handleSubmitEmailChange = async (e) => {
        e.preventDefault();
        // Ajoutez la logique pour changer l'adresse e-mail de l'utilisateur
    };

    const handleSubmitPasswordChange = async (e) => {
        e.preventDefault();
        // Ajoutez la logique pour changer le mot de passe de l'utilisateur
    };

    return (
        <div>
            <h2>User Profile</h2>
            <div>
                <p>Name: {user.username}</p>
                <p>Company: {user.company}</p>
                <p>Email: {user.email}</p>
            </div>
            <div>
                <h3>Change Email</h3>
                <form onSubmit={handleSubmitEmailChange}>
                    <input type="email" placeholder="New Email" value={newEmail} onChange={handleEmailChange} />
                    <button type="submit">Change Email</button>
                </form>
            </div>
            <div>
                <h3>Change Password</h3>
                <form onSubmit={handleSubmitPasswordChange}>
                    <input type="password" placeholder="New Password" value={newPassword} onChange={handlePasswordChange} />
                    <input type="password" placeholder="Confirm Password" value={confirmPassword} onChange={handleConfirmPasswordChange} />
                    <button type="submit">Change Password</button>
                </form>
            </div>
            {error && <div>{error}</div>}
        </div>
    );
}

export default Profile;
