import React, { createContext, useContext, useState } from "react";
import axios from 'axios';

// Création du contexte d'authentification (authContext)
export const authContext = createContext();

// Hook d'utilisation du contexte d'authentification
export const useAuth = () => {
    const context = useContext(authContext);
    if (!context) throw new Error('There is no AuthProvider');
    return context;
}

// Création d'un client Axios avec la configuration de base
const client = axios.create({
    baseURL: 'http://127.0.0.1:8000/api/',
});

// Fonction pour obtenir le token d'authentification depuis le stockage local (localStorage ou sessionStorage)
function getAuthToken() {
    return localStorage.getItem('token');
}

// Ajouter un intercepteur pour inclure le token d'authentification dans les requêtes
client.interceptors.request.use(
    (config) => {
        const token = getAuthToken();
        if (token) {
            config.headers['Authorization'] = `Token ${token}`;
        }
        return config;
    },
    (error) => Promise.reject(error)
);

// Composant fournisseur d'authentification
export function AuthProvider({ children }) {
    const [loading, setLoading] = useState(false);
    const [user, setUser] = useState({
        logged: false,
        data: [],
    });

    // Fonction de connexion
    const login = async (data) => {
        setLoading(true);
        try {
            const response = await client.post('login/', data);
            const token = response.data.token;
            console.log('Token:', token);
            localStorage.setItem('token', token);
            setUser({ logged: true, data: response.data });
        } catch (err) {
            console.error('Login error:', err);
            throw new Error('Login failed');
        } finally {
            setLoading(false);
        }
    }

    // Fonction de déconnexion
    const logout = async () => {
        if (!user.logged) return console.log("User is not logged");
        setLoading(true);
        try {
            await client.post('logout/');
            localStorage.removeItem('token');
            setUser({ logged: false, data: [] });
        } catch (err) {
            console.error(err);
            throw new Error('Logout failed');
        } finally {
            setLoading(false);
        }
    }

    // Fonction d'inscription
    const signup = async (data) => {
        setLoading(true);
        try {
            const res = await client.post('register/', data);
            return res.data;
        } catch (err) {
            console.error(err);
            throw new Error('Signup failed');
        } finally {
            setLoading(false);
        }
    }

    // Fonction de réinitialisation du mot de passe
    const resetPassword = async (email) => {
        setLoading(true);
        try {
            const res = await client.post('/reset-password', { email });
            return res.data; // Retourne les données de la réponse
        } catch (err) {
            console.error(err);
            throw new Error('An error occurred while resetting the password');
        } finally {
            setLoading(false);
        }
    };

    // Fonction de suppression de l'utilisateur
    const deleteUser = async () => {
        setLoading(true);
        try {
            await client.delete('delete-user/');
            setUser({ logged: false, data: [] });
        } catch (err) {
            console.error(err);
            throw new Error('Failed to delete user');
        } finally {
            setLoading(false);
        }
    };

    // Valeur du contexte d'authentification
    const contextValue = { login, logout, user, setUser, signup, resetPassword, deleteUser, loading };

    return (
        <authContext.Provider value={contextValue}>
            {children}
        </authContext.Provider>
    );
}
