import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function ValidateUser() {
    const { userId } = useParams();
    const navigate = useNavigate();

    const handleValidation = async (isValidated) => {
        try {
            // Assurez-vous que userId contient une valeur avant de construire l'URL
            if (userId) {
                await axios.patch(`http://127.0.0.1:8000/api/validate/${userId}/`, { is_validated: isValidated });
                navigate('/admin');
            } else {
                console.error('Error: userId is undefined');
            }
        } catch (error) {
            console.error('Error updating validation status:', error);
        }
    };

    return (
        <div className="validationContainer">
            <h1>Validate User</h1>
            <button onClick={() => handleValidation(true)}>Accept</button>
            <button onClick={() => handleValidation(false)}>Reject</button>
        </div>
    );
}

export default ValidateUser;
