import axios from 'axios';
import api from './axiosConfig';
const API_URL = 'http://127.0.0.1:8000/api/users/';
const API_REGISTER_URL = 'http://127.0.0.1:8000/api/register/';
const API_LOGIN_URL = 'http://127.0.0.1:8000/api/login/';

// Fonction pour obtenir le token d'authentification depuis le stockage local (localStorage ou sessionStorage)
function getAuthToken() {
    return localStorage.getItem('token');
}



export async function getUsers() {
    try {
        const response = await api.get('users/');
        return response.data;
    } catch (error) {
        console.error('Error fetching users:', error.response || error.message || error);
        throw new Error('Failed to fetch users');
    }
}


export async function updateUser(userId, updatedData) {
    const authToken = getAuthToken();
    const url = `${API_URL}${userId}/`;
    try {
        const response = await axios.patch(url, updatedData, {
            headers: {
                Authorization: `Token ${authToken}`
            }
        });
        return response.data;
    } catch (error) {
        throw new Error(`Failed to update user with ID ${userId}`);
    }
}

export async function getUserById(userId) {
    const authToken = getAuthToken();
    const url = `${API_URL}${userId}/`;
    try {
        const response = await axios.get(url, {
            headers: {
                Authorization: `Token ${authToken}`
            }
        });
        return response.data;
    } catch (error) {
        throw new Error(`Failed to fetch user with ID ${userId}`);
    }
}

// Fonction pour créer un nouveau compte utilisateur
export async function registerUser(userData) {
    try {
        const response = await axios.post(API_REGISTER_URL, userData);
        return response.data;
    } catch (error) {
        throw new Error('Failed to register user');
    }
}

// Fonction pour se connecter et obtenir un token
export async function loginUser(username, password) {
    try {
        const response = await axios.post('http://127.0.0.1:8000/api/login/', {
            username: username,
            password: password
        });
        const token = response.data.token;
        localStorage.setItem('token', token);
        return token;
    } catch (error) {
        console.error('Login error:', error);
        throw new Error('Failed to login');
    }
}

// Exemple d'utilisation de la fonction loginUser
// loginUser('yourUsername', 'yourPassword');
