import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000/api/users/';

export async function getUsers() {
    try {
        const response = await axios.get(API_URL);
        return response.data;
    } catch (error) {
        throw new Error('Failed to fetch users');
    }
}

export async function updateUser(userId, updatedData) {
    const url = `${API_URL}${userId}/`;
    try {
        const response = await axios.patch(url, updatedData);
        return response.data;
    } catch (error) {
        throw new Error(`Failed to update user with ID ${userId}`);
    }
}
export async function getUserById(userId) {
    const url = `${API_URL}${userId}/`;
    try {
        const response = await axios.get(url);
        return response.data;
    } catch (error) {
        throw new Error(`Failed to fetch user with ID ${userId}`);
    }
}
// Définir l'URL de base de votre API pour la création de compte
const API_REGISTER_URL = 'http://127.0.0.1:8000/api/register/';

// Fonction pour créer un nouveau compte utilisateur
export async function registerUser(userData) {
    try {
        const response = await axios.post(API_REGISTER_URL, userData);
        return response.data;
    } catch (error) {
        // Gérer les erreurs lors de la création du compte utilisateur
        throw new Error('Failed to register user');
    }
}
